		return 0;

	b = (VRD_Y__BUFFER_STATE) vrd_y__flex_alloc( sizeof( struct vrd_y__buffer_state ) );
	if ( ! b )
		VRD_Y__FATAL_ERROR( "out of dynamic memory in vrd_y__scan_buffer()" );

	b->vrd_y__buf_size = size - 2;	/* "- 2" to take care of EOB's */
	b->vrd_y__buf_pos = b->vrd_y__ch_buf = base;
	b->vrd_y__is_our_buffer = 0;
	b->vrd_y__input_file = 0;
	b->vrd_y__n_chars = b->vrd_y__buf_size;
	b->vrd_y__is_interactive = 0;
	b->vrd_y__at_bol = 1;
	b->vrd_y__fill_buffer = 0;
	b->vrd_y__buffer_status = VRD_Y__BUFFER_NEW;

	vrd_y__switch_to_buffer( b );

	return b;
